DROP TABLE IF EXISTS `nuke_legal_cfg`;
CREATE TABLE `nuke_legal_cfg` (`contact_email` varchar(255) NOT NULL default 'legal@MySite.com', `contact_subject` varchar(255) NOT NULL default 'Legal Notice Inquiry', `country` varchar(255) NOT NULL default 'United States of America') TYPE=MyISAM;

DROP TABLE IF EXISTS `nuke_legal_docs`;
CREATE TABLE `nuke_legal_docs` (`did` int(11) NOT NULL auto_increment, `doc_name` varchar(32) NOT NULL, `doc_status` tinyint(4) NOT NULL default '0', PRIMARY KEY (`did`)) TYPE=MyISAM;

DROP TABLE IF EXISTS `nuke_legal_text`;
CREATE TABLE `nuke_legal_text` (`tid` int(11) NOT NULL auto_increment, `doc_text` text NOT NULL, PRIMARY KEY (`tid`)) TYPE=MyISAM;

DROP TABLE IF EXISTS `nuke_legal_text_map`;
CREATE TABLE `nuke_legal_text_map` (`mid` mediumint(9) NOT NULL, `did` int(11) NOT NULL, `tid` int(11) NOT NULL, `language` varchar(32) NOT NULL default 'english', UNIQUE KEY `mid` (`mid`,`did`,`tid`)) TYPE=MyISAM;
